mapper.select:
SELECT

FROM t_fenqile_order


        and order_no = #{orderNo}


        and payment_no = #{paymentNo}


        and remark = #{remark}


        and update_time = #{updateTime}


        and create_time = #{createTime}

SELECT tfo.id, tor.order_no, tfo.payment_no, tuser.nickname AS user_nickname, tor.contact_type, tor.contact_info, tuser.mobile AS user_mobile, suser.nickname AS service_user_nickname, suser.mobile AS service_user_mobile, tor.`status` AS order_status, tor. NAME AS product_name, tpro.unit, tpro.price, tpro.amount, IFNULL(tor.coupon_money, 0) AS coupon_money, tor.actual_money, tor.total_money, tosp.server_money, tosp.commission_money, tor.create_time, tfr.amount AS recon_amount, tfr.commission_money AS fenqile_commission_money, tfr.`status` AS recon_status, tfr.process_time, tfr.admin_name FROM t_fenqile_order tfo LEFT JOIN t_order tor ON tor.order_no = tfo.order_no LEFT JOIN t_user tuser ON tor.user_id = tuser.id LEFT JOIN t_user suser ON tor.service_user_id = suser.id LEFT JOIN t_order_product tpro ON tor.order_no = tpro.order_no LEFT JOIN t_fenqile_reconciliation tfr ON tor.order_no = tfr.order_no LEFT JOIN t_order_share_profit tosp ON tor.order_no = tosp.order_no
AND tor.create_time >= #{startTime}
AND tor.create_time <= #{endTime}
AND tor.order_no = #{orderNo}
AND tor.category_id = #{categoryId}
AND tfo.payment_no = #{paymentNo}
AND tfr.`status` = #{reconStatus}
AND tor.user_id = #{userId}
AND tor.service_user_id = #{serviceUserId}
AND tuser.mobile = #{userMobile}
AND suser.mobile = #{serviceUserMobile}
AND tor.status IN
#{item}

SELECT
count(
IF (tfr.`status` = 0, TRUE, NULL)
) AS un_recon_count,
sum(
IF (tfr.`status` = 0, amount, 0)
) AS un_recon_total_amount,
sum(amount) AS total_amount
FROM
t_fenqile_reconciliation tfr
LEFT JOIN `t_order` tor ON tfr.`order_no` = tor.`order_no`
WHERE
tor.`type` = 3

    AND tor.create_time >= #{startTime}


    AND tor.create_time <= #{endTime}


    AND tor.order_no = #{orderNo}


    AND tor.category_id = #{categoryId}


    AND tfo.payment_no = #{paymentNo}


    AND tfr.`status` = #{reconStatus}


    AND tor.user_id = #{userId}


    AND tor.service_user_id = #{serviceUserId}


    AND tuser.mobile = #{userMobile}


    AND suser.mobile = #{serviceUserMobile}


    AND tor.status IN

        #{item}
